import React, {useEffect, useState} from 'react';
import Header from "../components/Header";
import {Button, Container, Dropdown, Form, FormGroup} from "react-bootstrap";
import {getChecklists} from "../http/checklistApi";

const AddCheck = () => {
    const [checklists, setCheckLists] = useState([]);
    const [selectedChecklists, setSelectedChecklists] = useState([]);
    const [showDropdown, setShowDropdown] = useState(false)

    useEffect(() => {
        getChecklists().then(checklists => {
            const formattedChecklists = checklists.map(title => {
                return {
                    title,
                    checked: false,
                }
            })
            setCheckLists(formattedChecklists)
        });
    }, []);

    const handleCheckboxChange = (checklistTitle) => {
        const foundChecklist = checklists.find(checklist => checklist.title === checklistTitle);

        if(foundChecklist && !foundChecklist.checked) {
            setCheckLists(lists => {
                return lists.map(list => {
                    if(list.title === foundChecklist.title) {
                        return {title: list.title, checked: true};
                    }
                    return list;
                });
            });

        }
    };

    const handleDropdownToggle = (isOpen, metadata) => {
        if(!isOpen && metadata.source === "select") {
            console.log("must open")
            setShowDropdown(true);
        } else {
            setShowDropdown(isOpen);
        }
    };


    const renderChecklists = () => {
        return checklists.map(({title, checked}, index) => {
            return (
                <Dropdown.Item key={index}>
                    <Form.Check
                        type="checkbox"
                        label={title}
                        checked={checked}
                        onChange={() => handleCheckboxChange(title)}
                    />
                </Dropdown.Item>
            )
        });
    }

    return (
        <div>
            <Header/>
            <Container className="mt-5">
                <h1 className="mb-3">Добавить проверку</h1>
                <Form>
                    <FormGroup className="mb-3">
                        <Form.Label>Название компании</Form.Label>
                        <Form.Control type="text" placeholder="Введите название компании" />
                    </FormGroup>

                    <FormGroup className="mb-3">
                        <Form.Label>Дата проверки</Form.Label>
                        <Form.Control type="date" />
                    </FormGroup>

                    <Dropdown show={showDropdown} onToggle={(nextShow, meta) => handleDropdownToggle(nextShow, meta)}  className="mb-4">
                        <Dropdown.Toggle  variant="dark" id="dropdown-checkbox">
                            Выберите проверочные листы
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {renderChecklists()}
                        </Dropdown.Menu>
                    </Dropdown>

                    <Button>Сохранить</Button>
                </Form>
            </Container>
        </div>
    );
};

export default AddCheck;
