import React, {useEffect, useState} from 'react';
import Header from "../components/Header";
import {Button, Container, Dropdown, Form, FormGroup} from "react-bootstrap";
import {getChecklists} from "../http/checklistApi";

const AddCheck = () => {
    const [checklists, setCheckLists] = useState([]);
    const [selectedChecklists, setSelectedChecklists] = useState([]);
    const [showDropdown, setShowDropdown] = useState(false)

    useEffect(() => {
        getChecklists()
            .then(checklists => {
            setCheckLists(checklists)
        });
    }, []);

    // const handleCheckboxChange = (checklist) => {
    //     if (selectedChecklists.includes(checklist)) {
    //         setSelectedChecklists(selectedChecklists.filter(item => item !== checklist));
    //     } else {
    //         setSelectedChecklists([...selectedChecklists, checklist]);
    //     }
    // };

    // const handleDropdownToggle = (isOpen, event, metadata) => {
    //     if (metadata.source === 'select' && event.target.tagName === 'INPUT') {
    //         event.stopPropagation(); // Prevent dropdown from closing when checkbox is clicked
    //     } else {
    //         setShowDropdown(isOpen);
    //     }
    // };

    const renderChecklists = () => {
        return checklists.map((title, index) => (
            <Dropdown.Item key={index}>
                <Form.Check
                    type="checkbox"
                    label={title}
                    checked={selectedChecklists.includes(title)}
                    onChange={() => handleCheckboxChange(title)}
                />
            </Dropdown.Item>
        ));
    }

    return (
        <div>
            <Header/>
            <Container className="mt-5">
                <h1 className="mb-3">Добавить проверку</h1>
                <Form>
                    <FormGroup className="mb-3">
                        <Form.Label>Название компании</Form.Label>
                        <Form.Control type="text" placeholder="Введите название компании" />
                    </FormGroup>

                    <FormGroup className="mb-3">
                        <Form.Label>Дата проверки</Form.Label>
                        <Form.Control type="date" />
                    </FormGroup>

                    <Dropdown  className="mb-4">
                        <Dropdown.Toggle variant="dark" id="dropdown-checkbox">
                            Выберите проверочные листы
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {renderChecklists()}
                        </Dropdown.Menu>
                    </Dropdown>

                    <Button>Сохранить</Button>
                </Form>
            </Container>
        </div>
    );
};

export default AddCheck;
