import React, {useState} from 'react';
import {Button, Card, Container, Form} from "react-bootstrap";
import {handleChange} from "../utils/handleChangeInput";
import {NavLink} from "react-router-dom";
import {LOGIN_ROUTE} from "../utils/consts";
import {registration} from "../http/userApi";


const Registration = () => {
    const [userData, setUserData] = useState({
        email: "",
        password: "",
        name: "",
        lastName: "",
        patronymic: "",
    });


    const submit = async () => {
        const response = await registration(userData.email, userData.password,
            userData.name, userData.lastName, userData.patronymic);
    }


    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{height: window.innerHeight - 60}}
        >
            <Card style={{width: 600}} className="p-4">
                <h2 className="m-auto">Регистрация</h2>
                <Form className="d-flex flex-column">
                    <Form.Control
                        name="lastName"
                        className="mt-5"
                        placeholder="Введите фамилию"
                        value={userData.lastName}
                        onChange={e => handleChange(e.target.name, e.target.value, setUserData)}
                        required
                    />

                    <Form.Control
                        name="name"
                        className="mt-4"
                        placeholder="Введите имя"
                        value={userData.name}
                        onChange={e => handleChange(e.target.name, e.target.value, setUserData)}
                        required
                    />

                    <Form.Control
                        name="patronymic"
                        className="mt-4"
                        placeholder="Введите отчество"
                        value={userData.patronymic}
                        onChange={e => handleChange(e.target.name, e.target.value, setUserData)}
                        required
                    />

                    <Form.Control
                        name="email"
                        className="mt-4"
                        placeholder="Введите email"
                        value={userData.email}
                        onChange={e => handleChange(e.target.name, e.target.value, setUserData)}
                        required
                    />

                    <Form.Control
                        name="password"
                        className="mt-4"
                        type="password"
                        placeholder="Введите пароль"
                        value={userData.password}
                        onChange={e => handleChange(e.target.name, e.target.value, setUserData)}
                        required
                    />
                    <p className="align-self-start mt-2">У вас уже есть учетная запись?
                        <NavLink to={LOGIN_ROUTE}> Войти!</NavLink>
                    </p>

                    <Button
                        type="submit"
                        className="mt-3 align-self-end"
                        variant="success"
                        onClick={submit}
                    >
                        Зарегистрироваться
                    </Button>
                </Form>
            </Card>
        </Container>
    );
};

export default Registration;
